mapper.insert:
insert into post(boardIdx)
values(#{boardIdx})

    select last_insert_id();

mapper.select:
select memberIdx
from member
where memberEmail = #{memberEmail}

select f.postIdx, c.categoryPostName, f.postForumTitle, f.postForumRegDate, f.postForumHits, m.memberNickname from postForum as f join categoryPost as c on f.categoryPostIdx = c.categoryPostIdx join member as m on f.memberIdx = m.memberIdx join post as p on f.postIdx = p.postIdx where p.boardIdx = #{boardIdx}
and f.postForumTitle like concat('%', #{keyword}, '%')
and f.postForumContents like concat('%', #{keyword}, '%')
and (f.postForumTitle like concat('%', #{keyword}, '%') or f.postForumContents like concat('%', #{keyword}, '%'))
and m.memberNickname like concat('%', #{keyword}, '%')
order by f.postForumRegDate desc limit #{postsPerPage} offset #{offset}

mapper.update:
update postForum
set postForumHits = postForumHits + 1
where postIdx = #{postIdx}
